import React from 'react';
import {Card, FormLayout, Layout, Page, Stack} from '@shopify/polaris';
import ThemeSelection from '../../components/ThemeSelection/ThemeSelection';
import {themes} from '../../const/themes';

/**
 * A sample page generated by Avada
 *
 * @param  props
 * @return {React.FunctionComponent}
 */
export default function Settings(props) {
  const themesHalfLength = Math.ceil(themes.length / 2);
  return (
    <Page breadcrumbs={[{content: 'Back', url: '/'}]} title="Settings">
      <Layout>
        <Layout.Section>
          <Card title="Preset">
            <Card.Section>
              <FormLayout.Group>
                {themes.slice(0, themesHalfLength).map(theme => (
                  <Stack key={theme.title}>
                    <Stack.Item fill>
                      <ThemeSelection {...theme} />
                    </Stack.Item>
                  </Stack>
                ))}

                {themes.slice(themesHalfLength, themes.length).map(theme => (
                  <Stack key={theme.title}>
                    <Stack.Item fill>
                      <ThemeSelection {...theme} />
                    </Stack.Item>
                  </Stack>
                ))}
              </FormLayout.Group>
            </Card.Section>
          </Card>
        </Layout.Section>

        <Layout.Section secondary></Layout.Section>
      </Layout>
    </Page>
  );
}

Settings.propTypes = {};
